import React from 'react';
import { useNavigate } from 'react-router-dom';
import './InfoMap.css';
import { animals } from '../GalleryPage/animals'; // Asegúrate de importar 'animals' desde donde esté definido

const InfoMap = () => {
    const navigate = useNavigate();

    const handleAnimalClick = (animalKey) => {
        const animalInfo = animals[animalKey];
        if (animalInfo) {
            navigate('/accordion', { state: { animal: animalInfo } });
        } else {
            console.error('Animal info not found for:', animalKey);
        }
    };

    return (
        <div className="info-map">
            <h2>Animales Endémicos por Macrozona</h2>
            <ul>
                <h3>Norte</h3>
                {Object.keys(animals).map((key) => {
                    const animal = animals[key];
                    if (animal.macrozone === 'norte') {
                        return (
                            <li key={key}>
                                <a href="#norte" onClick={() => handleAnimalClick(key)}>{animal.title}</a>
                            </li>
                        );
                    }
                    return null;
                })}
                <br />
                <h3>Centro</h3>
                {Object.keys(animals).map((key) => {
                    const animal = animals[key];
                    if (animal.macrozone === 'centro') {
                        return (
                            <li key={key}>
                                <a href="#centro" onClick={() => handleAnimalClick(key)}>{animal.title}</a>
                            </li>
                        );
                    }
                    return null;
                })}
                <br />
                <h3>Centro-Sur</h3>
                {Object.keys(animals).map((key) => {
                    const animal = animals[key];
                    if (animal.macrozone === 'centroSur') {
                        return (
                            <li key={key}>
                                <a href="#centro-sur" onClick={() => handleAnimalClick(key)}>{animal.title}</a>
                            </li>
                        );
                    }
                    return null;
                })}
                <br />
                <h3>Sur</h3>
                {Object.keys(animals).map((key) => {
                    const animal = animals[key];
                    if (animal.macrozone === 'sur') {
                        return (
                            <li key={key}>
                                <a href="#sur" onClick={() => handleAnimalClick(key)}>{animal.title}</a>
                            </li>
                        );
                    }
                    return null;
                })}
                <br />
                <h3>Austral</h3>
                {Object.keys(animals).map((key) => {
                    const animal = animals[key];
                    if (animal.macrozone === 'austral') {
                        return (
                            <li key={key}>
                                <a href="#austral" onClick={() => handleAnimalClick(key)}>{animal.title}</a>
                            </li>
                        );
                    }
                    return null;
                })}
            </ul>
        </div>
    );
};

export default InfoMap;
