import React from 'react';
import { useNavigate } from 'react-router-dom';
import './InfoMap.css';
import { animals } from '../GalleryPage/animals'; // Asegúrate de importar 'animals' correctamente

const InfoMap = () => {
    const navigate = useNavigate();

    const handleAnimalClick = (animalKey) => (event) => {
        event.preventDefault(); // Evita la navegación por defecto del anchor
        const animalInfo = animals[animalKey];
        if (animalInfo) {
            navigate('/accordion', { state: { animal: animalInfo } });
        } else {
            console.error('Animal info not found for:', animalKey);
        }
    };

    const renderAnimalList = (region) => {
        return animals
            .filter(animal => animal.region === region)
            .map(animal => (
                <li key={animal.name}>
                    <a href={`#${region}`} onClick={handleAnimalClick(animal.name.toLowerCase().replace(/\s+/g, ''))}>
                        {animal.title}
                    </a>
                </li>
            ));
    };

    return (
        <div className="info-map">
            <h2>Animales Endémicos por Macrozona</h2>
            <ul>
                <h3>Norte</h3>
                {renderAnimalList('norte')}
                <br />
                <h3>Centro</h3>
                {renderAnimalList('centro')}
                <br />
                <h3>Centro-Sur</h3>
                {renderAnimalList('centro-sur')}
                <br />
                <h3>Sur</h3>
                {renderAnimalList('sur')}
                <br />
                <h3>Austral</h3>
                {renderAnimalList('austral')}
            </ul>
        </div>
    );
};

export default InfoMap;
