import React, { useState } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import './Accordion.css';
import { animals } from './animals'; // Asegúrate de que la ruta de importación sea correcta

// Componente para cada ítem del acordeón
const AccordionItem = ({ title, content }) => {
    const [isOpen, setIsOpen] = useState(false);
    
    const handleClick = () => {
        setIsOpen(!isOpen);
    };
    
    return (
        <div className="accordion-item">
            <button className="accordion-button" onClick={handleClick}>
                {title}
            </button>
            {isOpen && (
                <div className="accordion-content">
                    <p>{content}</p>
                </div>
            )}
        </div>
    );
};

// Componente Accordion
const Accordion = () => {
    const location = useLocation();
    const { animal } = location.state || {};

    if (!animal || !animal.name) {
        return <p>No se encontró información para este animal.</p>;
    }

    const animalKey = Object.keys(animals).find(key => animals[key].name.toLowerCase() === animal.name.toLowerCase());
    const animalInfo = animalKey ? animals[animalKey] : null;

    if (!animalInfo) {
        return <p>No se encontró información detallada para este animal.</p>;
    }

    return (
        <div className="accordion">
            <div className='titulo'><p>{animalInfo.name.toUpperCase()}</p></div>
            <img src={animalInfo.image} alt={animalInfo.name} style={{maxWidth: '100%', height: 'auto'}} />
            {animalInfo.info.length > 0 ? (
                animalInfo.info.map((item, index) => (
                    <AccordionItem 
                        key={index}
                        title={item.title}
                        content={item.content}
                    />
                ))
            ) : (
                <p>No hay información adicional disponible.</p>
            )}
        </div>
    );
};

export default Accordion;
