import React from 'react';
import { useNavigate } from 'react-router-dom';
import './InfoMap.css';

const InfoMap = ({ animalsByZone }) => {
    const navigate = useNavigate();

    const handleAnimalClick = (animal) => {
        navigate('/accordion', { state: { animal } });
    };

    return (
        <div className="info-map">
            <h2>Animales Endémicos por Macrozona</h2>
            <ul>
                {Object.entries(animalsByZone).map(([zone, animals]) => (
                    <div key={zone}>
                        <h3>{zone}</h3>
                        {animals.map(animal => (
                            <li key={animal.title}>
                                <a
                                    href="#"
                                    onClick={(e) => {
                                        e.preventDefault();
                                        handleAnimalClick(animal);
                                    }}
                                >
                                    {animal.title}
                                </a>
                            </li>
                        ))}
                        <br />
                    </div>
                ))}
            </ul>
        </div>
    );
};

export default InfoMap;
