import React, { useRef, useEffect } from 'react';

const CanvasComponent = () => {
  const canvas1Ref = useRef(null);
  const canvas2Ref = useRef(null);
  let ctx1, ctx2, twopi, parts, sizeBase, cw, ch, count, color;

  // Helper functions
  const rand = (min, max) => Math.random() * (max - min) + min;
  
  // Define a single color
  const colorFill = 'hsla(200, 50%, 50%, 1)';  // Example: light blue color

  const create = () => {
    sizeBase = cw + ch;
    count = Math.floor(sizeBase * 0.3);
    color = colorFill;

    ctx1.clearRect(0, 0, cw, ch);
    ctx1.globalCompositeOperation = 'lighter';
    while (count--) {
      const radius = rand(1, sizeBase * 0.04);
      const blur = rand(10, sizeBase * 0.04);
      const x = rand(0, cw);
      const y = rand(0, ch);

      ctx1.shadowColor = color;
      ctx1.shadowBlur = blur;
      ctx1.beginPath();
      ctx1.arc(x, y, radius, 0, twopi);
      ctx1.closePath();
      ctx1.fill();
    }

    parts.length = 0;
    for (let i = 0; i < Math.floor((cw + ch) * 0.03); i++) {
      parts.push({
        radius: rand(1, sizeBase * 0.03),
        x: rand(0, cw),
        y: rand(0, ch),
        angle: rand(0, twopi),
        vel: rand(0.1, 0.5),
        tick: rand(0, 10000)
      });
    }
  };

  const loop = () => {
    requestAnimationFrame(loop);

    ctx2.clearRect(0, 0, cw, ch);
    ctx2.globalCompositeOperation = 'source-over';
    ctx2.shadowBlur = 0;
    ctx2.drawImage(canvas1Ref.current, 0, 0);
    ctx2.globalCompositeOperation = 'lighter';

    let i = parts.length;
    ctx2.shadowBlur = 15;
    ctx2.shadowColor = color;
    while (i--) {
      const part = parts[i];

      part.x += Math.cos(part.angle) * part.vel;
      part.y += Math.sin(part.angle) * part.vel;
      part.angle += rand(-0.05, 0.05);

      ctx2.beginPath();
      ctx2.arc(part.x, part.y, part.radius, 0, twopi);
      ctx2.fillStyle = color;
      ctx2.fill();

      if (part.x - part.radius > cw) part.x = -part.radius;
      if (part.x + part.radius < 0) part.x = cw + part.radius;
      if (part.y - part.radius > ch) part.y = -part.radius;
      if (part.y + part.radius < 0) part.y = ch + part.radius;

      part.tick++;
    }
  };

  const resize = () => {
    cw = canvas1Ref.current.width = canvas2Ref.current.width = window.innerWidth;
    ch = canvas1Ref.current.height = canvas2Ref.current.height = window.innerHeight;
    create();
  };

  const click = () => {
    create();
  };

  useEffect(() => {
    ctx1 = canvas1Ref.current.getContext('2d');
    ctx2 = canvas2Ref.current.getContext('2d');
    twopi = Math.PI * 2;
    parts = [];
    
    resize();
    create();
    loop();

    window.addEventListener('resize', resize);
    window.addEventListener('click', click);

    return () => {
      window.removeEventListener('resize', resize);
      window.removeEventListener('click', click);
    };
  }, []);

  return (
    <div style={{ background: '#D84315', overflow: 'hidden', position: 'relative', width: '100vw', height: '100vh' }}>
      <canvas ref={canvas1Ref} style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, opacity: 0 }}></canvas>
      <canvas ref={canvas2Ref} style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, background: '#000' }}></canvas>
    </div>
  );
};

export default CanvasComponent;
